import './notifications__notification__close-button.styl';
import Block from '../../../block/block.js';

export default class CloseButton extends Block {
    constructor() {
        super(document.createElement('div'));
        this._element.classList.add('notifications__notification__close-button-container');

        this._closeButton = document.createElement('img');
        this._closeButton.classList.add('notifications__notification__close-button');
        this._closeButton.src = 'data:image/svg+xml;base64,';

        this._element.appendChild(this._closeButton);
    }
}
